visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });

  const isRecheckRequired = programEncounter.getObservationReadableValue('Recheck for TB?');
  console.log('isRecheckRequired',isRecheckRequired);
  if(isRecheckRequired){
  scheduleBuilder
    .add({
      name: "Sample collection - AFB negative followup",
      encounterType: "Sample collection",
      earliestDate: imports.moment(programEncounter.encounterDateTime).add(7, 'days').toDate(),
      maxDate: imports.moment(programEncounter.encounterDateTime).add(10, 'days').toDate()
    }).whenItem(isRecheckRequired == 'Yes')
    .is.truthy;

    scheduleBuilder
    .add({
      name: "Treatment Outcome",
      encounterType: "Treatment Outcome",
      earliestDate: imports.moment(programEncounter.encounterDateTime).add(7, 'days').toDate(),
      maxDate: imports.moment(programEncounter.encounterDateTime).add(10, 'days').toDate()
    }).whenItem(isRecheckRequired == 'No')
    .is.truthy;
    }




  return scheduleBuilder.getAll();
};
